<demo-block class="demo-box" :jsfiddle="{&quot;html&quot;:&quot;&lt;el-form :model=\&quot;ruleForm2\&quot; :rules=\&quot;rules2\&quot; ref=\&quot;ruleForm2\&quot; label-width=\&quot;100px\&quot; class=\&quot;demo-ruleForm\&quot;&gt;\n  &lt;el-form-item label=\&quot;密码\&quot; prop=\&quot;pass\&quot;&gt;\n    &lt;el-input type=\&quot;password\&quot; v-model=\&quot;ruleForm2.pass\&quot; auto-complete=\&quot;off\&quot;&gt;&lt;/el-input&gt;\n  &lt;/el-form-item&gt;\n  &lt;el-form-item label=\&quot;确认密码\&quot; prop=\&quot;checkPass\&quot;&gt;\n    &lt;el-input type=\&quot;password\&quot; v-model=\&quot;ruleForm2.checkPass\&quot; auto-complete=\&quot;off\&quot;&gt;&lt;/el-input&gt;\n  &lt;/el-form-item&gt;\n  &lt;el-form-item label=\&quot;年龄\&quot; prop=\&quot;age\&quot;&gt;\n    &lt;el-input v-model.number=\&quot;ruleForm2.age\&quot;&gt;&lt;/el-input&gt;\n  &lt;/el-form-item&gt;\n  &lt;el-form-item&gt;\n    &lt;el-button type=\&quot;primary\&quot; @click=\&quot;handleSubmit2\&quot;&gt;提交&lt;/el-button&gt;\n    &lt;el-button @click=\&quot;handleReset2\&quot;&gt;重置&lt;/el-button&gt;\n  &lt;/el-form-item&gt;\n&lt;/el-form&gt;\n\n&quot;,&quot;script&quot;:&quot;\n  export default {\n    data() {\n      var checkAge = (rule, value, callback) =&gt; {\n        if (!value) {\n          return callback(new Error('年龄不能为空'));\n        }\n        setTimeout(() =&gt; {\n          if (!Number.isInteger(value)) {\n            callback(new Error('请输入数字值'));\n          } else {\n            if (value &lt; 18) {\n              callback(new Error('必须年满18岁'));\n            } else {\n              callback();\n            }\n          }\n        }, 1000);\n      };\n      var validatePass = (rule, value, callback) =&gt; {\n        if (value === '') {\n          callback(new Error('请输入密码'));\n        } else {\n          if (this.ruleForm2.checkPass !== '') {\n            this.$refs.ruleForm2.validateField('checkPass');\n          }\n          callback();\n        }\n      };\n      var validatePass2 = (rule, value, callback) =&gt; {\n        if (value === '') {\n          callback(new Error('请再次输入密码'));\n        } else if (value !== this.ruleForm2.pass) {\n          callback(new Error('两次输入密码不一致!'));\n        } else {\n          callback();\n        }\n      };\n      return {\n        ruleForm2: {\n          pass: '',\n          checkPass: '',\n          age: ''\n        },\n        rules2: {\n          pass: [\n            { validator: validatePass, trigger: 'blur' }\n          ],\n          checkPass: [\n            { validator: validatePass2, trigger: 'blur' }\n          ],\n          age: [\n            { validator: checkAge, trigger: 'blur' }\n          ]\n        }\n      };\n    },\n    methods: {\n      handleReset2() {\n        this.$refs.ruleForm2.resetFields();\n      },\n      handleSubmit2(ev) {\n        this.$refs.ruleForm2.validate((valid) =&gt; {\n          if (valid) {\n            alert('submit!');\n          } else {\n            console.log('error submit!!');\n            return false;\n          }\n        });\n      }\n    }\n  }\n&quot;,&quot;style&quot;:null}">
